actionConfiguration.body:
(response) =>{
		return response.map((response) => {
			return {
				"Serial Number": response.subLot ? response.subLot.code : response.serialNumberInput,
				"Carrier Code": response.carrier ? response.carrier.code : "-",
				"Warehouse": response.storageLocation ? response.storageLocation.name : "-",
				"Validation": response.stocktakeValidation,
				"Action": queryTransforms.createActionButtonLabel(response.stocktakeValidation)
			}
		})
	}

