export default {
	getLocationList: () => {
		var locations = []
		for (let i = 0; i < GetWarehouseBins.data.data.getEquipment.children.length; i++) {
			var current = GetWarehouseBins.data.data.getEquipment.children[i]
			locations.push(current.name + ": no carrier")
			for (let j = 0; j < current.carriers.length; j++) {
				locations.push(current.name + ": " + current.carriers[j].code)
			}
		}
		return locations
	},
	getJobResponseList: () => {
		var locations = []
		for (let i = 0; i < GetStocktakeJobResponses.data.data.getJobOrder.children.length; i++ ) {
			var current = GetStocktakeJobResponses.data.data.getJobOrder.children[i]
			for (let j = 0; j < current.jobResponses.length; j ++) {
				if (current.jobResponses[j].materialActual.length > 0) {
					locations.push(current.equipment.name + ": " + current.jobResponses[j].materialActual[0].carrier.code)
				} else {
					locations.push(current.equipment.name + ": no carrier")
				}

			}
		}
		return locations
	},
	getCountedList: () => {
		var locations = []
		for (let i = 0; i < GetStocktakeCountedLocations.data.data.getJobOrder.children.length; i++ ) {
			var current = GetStocktakeCountedLocations.data.data.getJobOrder.children[i]
			for (let j = 0; j < current.jobResponses.length; j ++) {
				if (current.jobResponses[j].materialActual.length > 0) {
					locations.push(current.equipment.name + ": " + current.jobResponses[j].materialActual[0].carrier.code)
				} else {
					locations.push(current.equipment.name + ": no carrier")
				}

			}
		}
		return locations
	},
	getFinishedList: () => {
		var locations = []
		for (let i = 0; i < GetStocktakeFinalisedLocations.data.data.getJobOrder.children.length; i++ ) {
			var current = GetStocktakeFinalisedLocations.data.data.getJobOrder.children[i]
			for (let j = 0; j < current.jobResponses.length; j ++) {
				if (current.jobResponses[j].materialActual.length > 0) {
					let s = current.equipment.name + ": " + current.jobResponses[j].materialActual[0].carrier.code
					locations.push(String(s))
				} else {
					locations.push(String(current.equipment.name + ": no carrier"))
				}

			}
		}
		return locations
	},
	getJobOrderList: () => {
		var ret = []
		ret.push(GetStocktakeJobResponses.data.data.getJobOrder.id)
		for (let i = 0; i < GetStocktakeJobResponses.data.data.getJobOrder.children.length; i++) {
			ret.push(GetStocktakeJobResponses.data.data.getJobOrder.children[i].id)
		}
		return ret
	}
}
