actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			id:GetWarehouseStocktakes.data.data.queryJobOrder[0].id
		},
    query: `
query GetStocktakeFinalisedLocations($id:ID!){
  getJobOrder(id:$id){
    name
    children (filter:{dispatchStatus:{eq:ACTIVE}}){
      name
			equipment {
				name
			}
      jobResponses (filter:{not:{has:endDateTime}}){
        id 
        materialActual {
					carrier {
						code
						id
					}
				}
      }
    }
  }
}
    `
})
}}

