actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			name:appsmith.user.email
		},
    query: `
query($name:String!){
  getUser(username:$name){
    name
    hasRole@cascade{
      role{
        name
        hasGrantedRights(filter:{name:{eq:isAdmin}}){
          name
        }
      }
      responsibilities(filter:{equipmentLevel:{eq:Site}}){
        id
        name
      }
    }
  }
}
    `
})
}}

