actionConfiguration.body:
() => {
		const flattened = []
		GetUserSites.data.data.getUser.hasRole.map((userRole) => {
			userRole.responsibilities.map((site) => {
				flattened.push(site)
			})
		})
		const result = flattened.map((row)=>{
			return {label:row.name, value:row.id}
		})
		return result
		}

