actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			ids:StocktakeLists.getJobOrderList()
		},
    query: `
mutation CloseStocktake($ids:[ID!]!){
  updateJobOrder(input:{
    filter:{id:$ids},
    set:{dispatchStatus:CLOSED}
  }){numUids}
}
    `
})
}}

