actionConfiguration.body:
() => {
		var locations = []
		for (let i = 0; i < GetStocktakeCountedLocations.data.data.getJobOrder.children.length; i++ ) {
			var current = GetStocktakeCountedLocations.data.data.getJobOrder.children[i]
			for (let j = 0; j < current.jobResponses.length; j ++) {
				if (current.jobResponses[j].materialActual.length > 0) {
					locations.push(current.equipment.name + ": " + current.jobResponses[j].materialActual[0].carrier.code)
				} else {
					locations.push(current.equipment.name + ": no carrier")
				}

			}
		}
		return locations
	}

