    variables: {
			storage:selectStocktakeBin.selectedOptionLabel,
			carrier:selectStocktakeCarrier.selectedOptionValue
		},
    query: `
query GetEquipment($storage:String!,$carrier:ID!){
  getStockOnHand(filter:{carrier:{id:$carrier},storageLocation:{name:$storage}}){
