    }
  }
}
    `
})

JSON.stringify({
    variables: {
			equip:selectStocktakeBin.selectedOptionValue,
		},
    query: `
query QueryJobOrder($equip:[ID!]!) {
  queryJobOrder (filter:{dispatchStatus:{eq:ACTIVE}}) @cascade{
		id
		name
		parent {
			id
			name
		}
    equipment(filter:{id:$equip}) {
      id
    }
  }
}`
})

