export default {

	stocktakeCountTable: () => {
		if (GetStocktakeCounts.data.data != null) {
			var ret = []
			for (let i = 0; i < GetStocktakeCounts.data.data.getStocktakeCount.length; i ++) {
				var line = GetStocktakeCounts.data.data.getStocktakeCount[i]
				let row = {
					"Material": line.material.code,
					"Quantity": line.quantity
				}
				if (line.status != null) {
					row["Status"] = line.status.code
				}
				if (line.owner != null) {
					row["Owner"] = line.owner.name
				}
				ret.push(row)
			}
			return ret
		}
		return []
	},
	stockCountInput: () => {
		let ret = {
			"material":{"code": Validation.activeMaterial()},
			"stocktake": {"id": GetStocktakeJobOrder.data.data.queryJobOrder[0].parent.id},
			"jobResponse": {"id": HandleJobResponses.activeJobResponse()},
			"user": {"id": GetUserSites.data.data.getUser.id},
			"status": {"code": HandleOwnerStatus.getStatusCode()}
		}
		if (tabMaterialSelect.selectedTab == "Serialised") {
			ret.quantity = 1
			ret.subLot = {"code": inputSerialised.text}
		} else {
			ret.quantity = parseInt(inpStocktakeCount.text)
		}
		if (HandleOwnerStatus.getOwnerID() != "") {
			ret.owner = {"id": HandleOwnerStatus.getOwnerID()}
		}
		return ret
	},
	activeCarrier: () => {
		if (tabCarrierSwitch.selectedTab == "Stored on Carrier") {
			return selectStocktakeCarrier.selectedOptionLabel
		} else {
			return ""
		}
	},
	siteSelectDefault: () => {
		if (appsmith.URL.queryParams.Site != null){
			return appsmith.URL.queryParams.Site
		}
		return ""
	},
	warehouseSelectDefault: () => {
		if (appsmith.URL.queryParams.Warehouse != null){
			return appsmith.URL.queryParams.Warehouse
		}
		return ""
	},
	binSelectDefault: () => {
		if (appsmith.URL.queryParams.Location != null){
			let text = appsmith.URL.queryParams.Location.split(": ")
			return text[0]
		}
		return ""
	},
	carrierSelectDefault: () => {
		if (appsmith.URL.queryParams.Location != null){
			let text = appsmith.URL.queryParams.Location.split(": ")
			if (text[1] == "no carrier") {
				return ""
			} else {
				return text[1]
			}
		}
		return ""
	},
	stockedMaterialsList: () => {

		var ret = []

		if (GetMaterialsAtLocation.data == null) {
			return ret
		}

		for (let i = 0; i < GetMaterialsAtLocation.data.data.getStockOnHand.length; i++) {
			if (GetMaterialsAtLocation.data.data.getStockOnHand[i].material.value == "") {
				let line = {
					"label": GetMaterialsAtLocation.data.data.getStockOnHand[i].material.label,
					"value": String(i)
				}
				ret.push(line)
			} else {
				let line = {
					"label": GetMaterialsAtLocation.data.data.getStockOnHand[i].material.label,
					"value": GetMaterialsAtLocation.data.data.getStockOnHand[i].material.value
				}
				ret.push(line)
			}
		}
		return ret
	}

}
