export default {

	canSubmit: () => {
		if (selectStocktakeBin.selectedOptionValue == "") {
			return false
		}
		if (inpStocktakeCount.text == null) {
			return false
		}

		if (tabMaterialSelect.selectedTab == "Serialised") {
			if (inputSerialised.text == "") {
				return false
			}
		} else {
			if (parseInt(inpStocktakeCount.text) == 0) {
				return false
			}
			if (tabMaterialSelect.selectedTab == "Stocked Materials") {
				if (selectStockedMaterial.selectedOptionLabel == "") {
					return false
				}
			} else {
				if (selectUnstockedMaterial.selectedOptionLabel == "") {
					return false
				}
			}
		}

		if (HandleJobResponses.activeJobResponseIsClosed()) {
			return false
		}
		return true
	},
	countOpenText: () => {
		if (selectStocktakeBin.selectedOptionValue == "") {
			return ""
		}
		if (GetStocktakeJobOrder.data.data.queryJobOrder == null) {
			return "stocktake count is closed at this bin"
		}
		if (HandleJobResponses.activeJobResponseIsClosed()) {
			return "location count has already been finalised"
		}
		return "active count"
	},
	activeMaterial: () => {
		if (tabMaterialSelect.selectedTab == "Stocked Materials") {
			return selectStockedMaterial.selectedOptionLabel
		} else {
			return selectUnstockedMaterial.selectedOptionLabel
		}
	}
}
