actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: {
   	material:{"code": selectStockedMaterial.selectedOptionLabel},
    stocktake: {"id": GetStocktakeJobOrder.data.data.queryJobOrder[0].id},
		jobResponse: {"id": HandleJobResponses.activeJobResponse()},
		user: {"id": GetUserSites.data.data.getUser.id},
		owner: {"id": HandleOwnerStatus.getOwnerID()},
  	status: {"code": HandleOwnerStatus.getStatusCode()},
		quantity: parseInt(inpStocktakeCount.text)
}
		},
    query: `
mutation AddStocktakeCount($input:[AddStocktakeCountInput!]!){
  addStocktakeCount(input:$input){
    quantity
  }
}
    `
})
}}

