actionConfiguration.body:
{{
JSON.stringify({
    variables: {

  input: {
    startDateTime: moment(),
    jobOrder: {"id": GetStocktakeJobOrder.data.data.queryJobOrder[0].id},
    equipment: {"id": selectStocktakeBin.selectedOptionValue}
	}
		},
    query: `
mutation AddJobResponseCarrier($input:[AddJobResponseInput!]!){
  addJobResponse(
    input:$input
  ) {
    jobResponse {
      id
    }
  }
}
    `
})
}}

