JSON.stringify({
    variables: {
			name:appsmith.user.email
		},
    query: `
query($name:String!){
  getUser(username:$name){
		id
    name
    hasRole@cascade{
      role{
        name
        hasGrantedRights(filter:{name:{eq:isAdmin}}){
          name
        }
      }
      responsibilities(filter:{equipmentLevel:{eq:Site}}){
        id
        name
      }
    }
  }
}
    `
})

async () => {
		var table = []
		storeValue("quantitiesTable", table)
		storeValue("erroredSerialsTable", table)
		storeValue("missingSerialsTable", table)
		storeValue("unexpectedSerialsTable", table)
		storeValue("validSerialsTable", table)
		if (appsmith.URL.queryParams.Site != null) {

			if (Format.carrierSelectDefault() != "") {
				return GetUserSites.run()
				.then(() => GetSiteWarehouses.run())
				.then(() => GetWarehouseBins.run())
				.then(() => HandleSelects.selectBin())
				.then(() => HandleSelects.selectCarrier())
			} else {
				return GetUserSites.run()
				.then(() => GetSiteWarehouses.run())
				.then(() => GetWarehouseBins.run())
				.then(() => HandleSelects.selectBin())
			}
		} 
		return GetUserSites.run()
	}

JSON.stringify({
    variables: {	
  input: HandleJobResponses.activeJobResponse()
		},
    query: `
query GetStocktakeComparison($input:ID!){
  getStoctakeActualComparison(input:{id:$input}){
    quantities {
      material{id,code}
			status{id,code}
			owner{id,name}
      counted
      expected
    }
    serialised {
      missingSubLots{
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
      }
      unexpectedSubLots {
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
				carrier{code}
				storageLocation{name}
      }
      erroredSubLots {
        error
        subLot {id,code}
				material {code}
				expectedLocation {name}
				countedLocation {name}
				expectedCarrier {code}
				countedCarrier {code}
				expectedStatus {code}
				countedStatus {code}
				expectedOwner {name}
				countedOwner {name}
      }
      validSubLots {
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
      }
    }
  }
}`
