JSON.stringify({
    variables: {
			equip:selectReportBin.selectedOptionValue,
		},
    query: `
query QueryJobOrder($equip:[ID!]!) {
  queryJobOrder (filter:{dispatchStatus:{eq:ACTIVE}}) @cascade{
		id
		name
		parent {
			id
			name
		}
    equipment(filter:{id:$equip}) {
      id
    }
  }
}`
})

JSON.stringify({
    variables: {
			name:appsmith.user.email
		},
    query: `
query($name:String!){
  getUser(username:$name){
		id
    name
    hasRole@cascade{
      role{
        name
        hasGrantedRights(filter:{name:{eq:isAdmin}}){
          name
        }
      }
      responsibilities(filter:{equipmentLevel:{eq:Site}}){
        id
        name
      }
    }
  }
}
    `
})

