export default {

	createStocktakeTable: () => {
		if (GetStocktakeCounts.data == null) {
			let ret = []
			return ret
		}
		if (GetStockOnHand.data == null) {
			let ret = []
			return ret
		}
		var ret = []
		for (let i = 0; i < GetStocktakeCounts.data.data.getStocktakeCount.length; i++) {
			let count = GetStocktakeCounts.data.data.getStocktakeCount[i]
			let found = false
			for (let j = 0; j < GetStockOnHand.data.data.getStockOnHand.length; j++) {
				let hand = GetStockOnHand.data.data.getStockOnHand[j]
				if (count.material.id == hand.material.id && count.status.id == hand.status.id) {

					if (count.owner == null) {
						if (hand.owner == null) {
							found = true
							let line = {
								"Material": count.material.code,
								"Status": count.status.code,
								"Owner": "",
								"Expected Count": hand.quantity,
								"Counted": count.quantity,
								"Adjustment Quantity": count.quantity - hand.quantity 
							}
							ret.push(line)
							break
						}
					} else {
						if (hand.owner != null) {
							if (hand.owner.id == count.owner.id) {
								found = true
								let line = {
									"Material": count.material.code,
									"Status": count.status.code,
									"Owner": count.owner.name,
									"Expected Count": hand.quantity,
									"Counted": count.quantity,
									"Adjustment Quantity": count.quantity - hand.quantity 
								}
								ret.push(line)
								break
							}
						}
					}
				}

			}
			if (!found) {
				if (count.owner == null) {
					let line = {
						"Material": count.material.code,
						"Status": count.status.code,
						"Owner": "",
						"Expected Count": 0,
						"Counted": count.quantity,
						"Adjustment Quantity": count.quantity
					}
					ret.push(line)
				} else {
					let line = {
						"Material": count.material.code,
						"Status": count.status.code,
						"Owner": count.owner.name,
						"Expected Count": 0,
						"Counted": count.quantity,
						"Adjustment Quantity": count.quantity
					}
					ret.push(line)
				}
			}
		}
		return ret
	}

}
