export default {

	createStocktakeTable: () => {
		if (GetStocktakeCounts.data == null) {
			let ret = []
			return ret
		}
		if (GetStockOnHand.data == null) {
			let ret = []
			return ret
		}
		var ret = []
		var foundStockOnHand = []
		for (let i = 0; i < GetStockOnHand.data.data.getStockOnHand.length; i ++) {
			foundStockOnHand.push(false)
		}

		for (let i = 0; i < GetStocktakeCounts.data.data.getStocktakeCount.length; i++) {
			let count = GetStocktakeCounts.data.data.getStocktakeCount[i]
			let found = false
			for (let j = 0; j < GetStockOnHand.data.data.getStockOnHand.length; j++) {
				let hand = GetStockOnHand.data.data.getStockOnHand[j]
				if (count.material.id == hand.material.id && count.status.id == hand.status.id) {

					if (count.owner == null) {
						if (hand.owner == null) {
							found = true
							foundStockOnHand[j] = true
							let line = {
								"Material": count.material.code,
								"Status": count.status.code,
								"Owner": "",
								"OwnerId": "",
								"Expected Count": hand.quantity,
								"Counted": count.quantity,
								"AdjustmentQuantity": count.quantity - hand.quantity,
								"Comment": ""
							}
							ret.push(line)
							break
						}
					} else {
						if (hand.owner != null) {
							if (hand.owner.id == count.owner.id) {
								found = true
								foundStockOnHand[j] = true
								let line = {
									"Material": count.material.code,
									"Status": count.status.code,
									"Owner": count.owner.name,
									"OwnerId": count.owner.id,
									"Expected Count": hand.quantity,
									"Counted": count.quantity,
									"AdjustmentQuantity": count.quantity - hand.quantity,
									"Comment": ""
								}
								ret.push(line)
								break
							}
						}
					}
				}

			}
			if (!found) {
				if (count.owner == null) {
					let line = {
						"Material": count.material.code,
						"Status": count.status.code,
						"Owner": "",
						"OwnerId": "",
						"Expected Count": 0,
						"Counted": count.quantity,
						"AdjustmentQuantity": count.quantity,
						"Comment": ""
					}
					ret.push(line)
				} else {
					let line = {
						"Material": count.material.code,
						"Status": count.status.code,
						"Owner": count.owner.name,
						"OwnerId": count.owner.id,
						"Expected Count": 0,
						"Counted": count.quantity,
						"AdjustmentQuantity": count.quantity,
						"Comment": ""
					}
					ret.push(line)
				}
			}
		}

		for (let i = 0; i < GetStockOnHand.data.data.getStockOnHand.length; i ++) {
			let hand = GetStockOnHand.data.data.getStockOnHand[i]
			if (!foundStockOnHand[i]) {
				let line = {
					"Material": hand.material.code,
					"Status": hand.status.code,
					"Owner": hand.owner.name,
					"OwnerId": hand.owner.id,
					"Expected Count": hand.quantity,
					"Counted": 0,
					"AdjustmentQuantity": hand.quantity*-1,
					"Comment": ""
				}
				ret.push(line)
			}

		}

		storeValue("table", ret)
		return ret
	},

	stocktakeAdjustmentInput: () => {
		let ret = []
		let table = appsmith.store.table
		for (let i = 0; i < table.length; i ++) {

			if (table[i].Owner == "") {
				let line = {
					"material": {"code": table[i].Material},
					"status": {"code": table[i].Status},
					"jobResponse": {"id": HandleJobResponses.activeJobResponse()},
					"quantity": table[i].AdjustmentQuantity,
					"user": {"id": GetUserSites.data.data.getUser.id}
				}
				ret.push(line)
			} else {
				let line = {
					"material": {"code": table[i].Material},
					"status": {"code": table[i].Status},
					"jobResponse": {"id": HandleJobResponses.activeJobResponse()},
					"quantity": table[i].AdjustmentQuantity,
					"owner": {"id": table[i].OwnerId},
					"user": {"id": GetUserSites.data.data.getUser.id}
				}
				ret.push(line)
			}

		}
		return ret
	}

}
