export default {


	selectBin: () => {
		GetBinCarriers.run()
		return GetStocktakeJobOrder.run()
		.then(() => GetJobOrderJobResponses.run())
		.then(() => GetStockOnHand.run())
		.then(() => GetStocktakeCounts.run())
		.then(() => Format.createStocktakeTable())
	},
	selectCarrier: () => {
		return GetStockOnHand.run()
		.then(() => GetStocktakeCounts.run())
		.then(() => Format.createStocktakeTable())
	},
	adjustStock: () => {
		return StocktakeAdjustment.run()
		.then(() => GetStockOnHand.run())
		.then(() => GetStocktakeCounts.run())
		.then(() => Format.createStocktakeTable())
	},
	addCommentSelect: () => {
		var table = appsmith.store.table
		table[tableStocktakeReport.selectedRowIndex].Comment = inputAddComment.text
		storeValue("table",table)
		closeModal("modalAddComment")
	},
	setAdjustmentSelect: () => {
		var table = appsmith.store.table
		table[tableStocktakeReport.selectedRowIndex].AdjustmentQuantity = parseInt(inputSetAdjustmnet.text)
		storeValue("table",table)
		closeModal("modalSetAdjustment")
	},
	closeJobResponse: () => {
		closeModal("modalConfirmCloseCount")
		CloseJobResponse.run()
		.then(GetJobOrderJobResponses.run())
	}

}
