actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input:[HandleJobResponses.activeJobResponse()],
			time:moment(),
		},
    query: `
mutation CloseJobResponse($input:[ID!]!,$time:DateTime!){
  updateJobResponse(input:{
    filter:{id:$input},
    set:{endDateTime:$time}
  }){
    numUids
  }
}`
})
}}

