actionConfiguration.body:
() => {
		if (GetStocktakeComparisons.data == null) {
			let ret = []
			return ret
		}
		var ret = []
		for (let i = 0; i < GetStocktakeComparisons.data.data.getStoctakeActualComparison.serialised.validSubLots.length ; i ++) {
			let line = GetStocktakeComparisons.data.data.getStoctakeActualComparison.serialised.validSubLots[i]
			let owner = ""
			if (line.owner != null) {
				owner = line.owner.companyCode
			}
			let row = {
				"Serial": line.materialSubLot.code,
				"Material": line.material.code,
				"Owner": owner,
				"Status": line.status.code,
			}
			ret.push(row)
		}
		storeValue("validSerialsTable", ret)
		return ret
	}

