actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			stocktake:GetStocktakeJobOrder.data.data.queryJobOrder[0].parent.name,
			subLot:tableMissingSerials.tableData[tableMissingSerials.selectedRowIndex].Serial
		},
    query: `
query GetStocktakeCount($stocktake:String!,$subLot:String!){
  getStocktakeCount(input:{stocktakeName:$stocktake,filter:{subLot:{code:$subLot},
    groupBy:{storageLocation:true, subLot:true,carrier:true,status:true,owner:true}}})
  {
  	quantity  
    storageLocation{name}
    carrier{code}
    status{code}
    owner{companyCode}
  }
}
    `
})
}}

