actionConfiguration.body:
{{
JSON.stringify({
    variables: {	
  input: HandleJobResponses.activeJobResponse()
		},
    query: `
query GetStocktakeComparison($input:ID!){
  getStoctakeActualComparison(input:{id:$input}){
    quantities {
      material{id,code}
			status{id,code}
			owner{id,name}
      counted
      expected
    }
    serialised {
      missingSubLots{
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
      }
      unexpectedSubLots {
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
				carrier{code}
				storageLocation{name}
      }
      erroredSubLots {
        error
        subLot {id,code}
				material {code}
				expectedLocation {name}
				countedLocation {name}
				expectedCarrier {code}
				countedCarrier {code}
				expectedStatus {code}
				countedStatus {code}
				expectedOwner {name}
				countedOwner {name}
      }
      validSubLots {
        materialSubLot{code}
				material{code}
				owner{name}
				status{code}
      }
    }
  }
}`
})
}}

