actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			input: {
  			stocktakeName: GetStocktakeJobOrder.data.data.queryJobOrder[0].parent.name,
    		filter: {
					jobResponse: {id:HandleJobResponses.activeJobResponse()},
      		groupBy: {
        		carrier: true,
        		owner: true,
        		material: true,
        		status: true,
        		storageLocation: true
      	}
    }
	}
		},
    query: `
query GetStocktakeCount($input:GetStocktakeCountInput!){
  getStocktakeCount(input:$input) {
    material{id,code},
    owner{id,name},
    status{id,code},
    quantity
  }
}`
})
}}

