actionConfiguration.body:
{{
JSON.stringify({
    variables: {
			carrierCode: CarrierSelect.selectedOptionValue
    },
    query: `
				query fetchWarehouse($carrierCode: String!){
  queryEquipment @cascade{
    label,
    name,
    children{
			name
			label
      carriers(filter:{code:{eq:$carrierCode}}){
        code
      }
    }
  }
}
		`}) }}

