actionConfiguration.body:
(validationToken) => {

		const red = `#f29582`
		const yellow = `#e1f248`
		const green = `#24bf31` 
		switch (validationToken) {
				case "SERIAL_NOT_FOUND":{
					return {
						serialColor: red,
						carrierColor: red,
						warehouseColor:red,
						actionColor: red,
					}
				}
				case "SERIAL_FOUND":{
					return {
						serialColor: green,
						carrierColor: green,
						warehouseColor: green,
						actionColor: green,
					}
				}	
		}
	}

